package com.class06;

public class NotOperator {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		boolean rain=true;
		
		if(!rain) {
			System.out.println("i will go tot the park");
		}
		
		boolean boo=!true;
		System.out.println(boo);//flase
		boolean boo1=!false;
		System.out.println(boo1);//true
		
		boolean traffic=false;
		if (!traffic) {
			System.out.println("I will come to the meeting on time");
		}
		
		
		String day="wednesday";
		if(!day.equals("Friday")) {
			System.out.println("Today is not friday");
		}
		
		
		
		boolean homework=false;
		if(!homework) {
			System.out.println("Ill be happy");
		}
		
		
		//not ooperator always is placed before the condition
		
		
		
		
	}

}
