package com.class08;

public class LoopNested {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

	for(int i=1;i<3;i++) {//outer loop 
		
		System.out.println("^^^^^^^^^^^^^^^^^^^^^^^^^");
		System.out.println("^^^^^^^^^^^^^^^^^^^^^^^^^");
		System.out.println("^^^^^^^^^^^^^^^^^^^^^^^^^");
		
		for(int j=1; j<=3;j++) {
		System.out.println("i am inner loop");	
		}
		
		
	}
		
		
		
		
		
		
		
		
		
		
	}

}
